export const initialState = {
    isLoggingIn: false, //로그인 시도 중
    isLoggingOut: false, //로그아웃 시도 중
    isLoggedIn: false,
    me: null,
    signUpData: {},
    loginData: {},
}


export const loginRequestAction = (data) => {
    return {
        type: 'LOG_IN_REQUEST',
        data,
    }
}

// export const loginSuccessAction = (data) => {
