import {all, fork, call, take, put, delay, debounce, throttle, takeLatest} from 'redux-saga/effects'
import axios from 'axios'
import { LOG_IN_REQUEST, LOG_IN_SUCCESS, LOG_IN_FAILURE, 
         LOG_OUT_REQUEST, LOG_OUT_SUCCESS, LOG_OUT_FAILURE, 
         SIGN_UP_FAILURE, SIGN_UP_REQUEST, SIGN_UP_SUCCESS } from '../reducers/user'

function loginAPI() {
    return axios.post('/api/login', data)
}

function* logIn(action) {
    try {
        yield delay(1000)
        // const result = yield call(loginAPI, action.data)
        yield put({
            type: LOG_IN_SUCCESS,
            data: action.data,
        });
    } catch(err) {
        yield put({
            type: LOG_IN_FAILURE,
            error: err.response.data,
        })
    }
}

function logOutAPI() {
    return axios.post('/api/logout')
}

function* logOut() {
    try {
        yield delay(1000)
        // const result = yield call(logOutAPI)
        yield put({
            type: LOG_OUT_SUCCESS,
            // data: result.data,
        });
    } catch(err) {
        yield put({
            type: LOG_OUT_FAILURE,
            error: err.response.data,
        })
    }
}

function* watchLogIn(){
    yield takeLatest(LOG_IN_REQUEST, logIn);
}

function* watchLogOut(){
    yield takeLatest(LOG_OUT_REQUEST, logOut);
}

function* watchSingUp(){
    yield takeLatest(SIGN_UP_REQUEST, logOut);
}

export default function* userSaga() {
    yield all([
        fork(watchLogIn),
        fork(watchLogOut),
        fork(watchSingUp),
    ])

}
