import { ShrinkOutlined } from "@ant-design/icons";

export const initialState = {
    mainPosts: [{
        id: 1,
        User: {
            id: 1,
            nickname: 'HonestJJ',
        },
        content: "My first 게시물 #해시태그 #익스프레스",
        Images: [{
                src: 'https://w.namu.la/s/f897e95aa25785a1b54cb0c9f328b37cb05449a8c9f815507198ef79cc6cc4a1a68a52522f629c9025adde63b28493fc63f442b085f37acdd3431e4f12568c77c47abdbe3e6452eb6c1313753ce195ee',
            },  {
                src: 'https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2FeqqBpc%2Fbtq7bDXOxzg%2Fm9K4sPYXsOme3nkPVK2wkk%2Fimg.png',
            }, {
                src: 'https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2FeqqBpc%2Fbtq7bDXOxzg%2Fm9K4sPYXsOme3nkPVK2wkk%2Fimg.png',
            }, ],
        Comments: [{
            User: {
                nickname: 'Jake'
            },
                content: '리액트 1',
            }, {
            User: {
                nickname: 'Honest JJ'
            },
                content: '리액트 2'
            }]
    }],
    imagePaths: [],
    addPostLoading: false,
    addPostDone: false,
    addPostError: null,
    addCommentLoading: false,
    addCommentDone: false,
    addCommentError: null,
}

export const LOAD_POSTS_REQUEST = 'LOAD_POSTS_REQUEST';
export const LOAD_POSTS_SUCCESS = 'LOAD_POSTS_SUCCESS';
export const LOAD_POSTS_FAILURE = 'LOAD_POSTS_FAILURE';

export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';

export const REMOVE_POST_REQUEST = 'REMOVE_POST_REQUEST';
export const REMOVE_POST_SUCCESS = 'REMOVE_POST_SUCCESS';
export const REMOVE_POST_FAILURE = 'REMOVE_POST_FAILURE';

export const ADD_COMMENT_REQUEST = 'ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_COMMENT_FAILURE';


export const addPost = (data) => ({
    type: ADD_POST_REQUEST,
    data,
})

export const addComment = (data) => ({
    type: ADD_COMMENT_REQUEST,
    data,
})

const dummyPost = (data) => ({
    id:2,
    content: '더미 데이터1',
    User: {
        id:1,
        nickname: 'HonestJJ',
    },
    Images: [],
    Comments: [],
})

const dummyComment = (data) => ({
    id : ShrinkOutlined.generate(),
    content: data,
    User: {
        id:1,
        nickname: 'HonestJJ',
    },
})

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST_REQUEST:
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null,
            };
        case ADD_POST_SUCCESS:    
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],
                addPostLoading: false,
                addPostDone: true,
            };
        case ADD_POST_FAILURE: 
        return{
            ...state,
            addPostLoading: false,
            addPostError: action.error,
            };
        case ADD_COMMENT_REQUEST:
            return {
                ...state,
                addCommentLoading: true,
                addCommentDone: false,
                addCommentError: null,
            };
        case ADD_COMMENT_SUCCESS:    
            return {
                ...state,
                addCommentLoading: false,
                addCommentDone: true,
            };
        case ADD_COMMENT_FAILURE: 
        return{
            ...state,
            addCommentLoading: false,
            addCommentError: action.error,
            };
        default:
            return state;
        };
}

export default reducer
