export const initialState = {
    logInLoading: false, //로그인, 로그아웃, 회원가입 로딩
    logInDone: false, 
    logInError: null,
    logOutLoading: false, //로그인, 로그아웃, 회원가입 로딩
    logOutDone: false, 
    logOutError: null,
    singUpLoading: false, //로그인, 로그아웃, 회원가입 로딩
    singUpDone: false, 
    singUpError: null,
    me: null,
    signUpData: {},
    loginData: {},
}

const dummyUser = (data) => ({
    ...data,
    nickname: 'HonestJJ',
    id: 1,
    posts: [],
    Following: [],
    Followers: [],
})

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST'
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS'
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE'

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST'
