const reducer = (state = initialState, action) => {
    switch (action.type) {
        case LOG_IN_REQUEST:
            return {
                    ...state,
                    logInLoading: true,
                    logInError: null,
                    logInDone: false,
            }
        case LOG_IN_SUCCESS:
            return {
                    ...state,
                    logInLoading: false,
                    logInDone: true,
                    me: dummyUser(action.data),                
            }
        case LOG_IN_FAILURE:
            return {
                    ...state,
                    logInLoading: false,
                    logInDone: action.error,
            }
        case LOG_OUT_REQUEST:
            return {
                    ...state,
                    logOutLoading: true,
                    logOutDone: false,
                    logOutError: null
            }
        case LOG_OUT_SUCCESS:
            return {
                    ...state,
                    logOutLoading: false,
                    logInLoading: false,
                    me: null,
            }
        case LOG_OUT_FAILURE:
            return {
                    ...state,
                    logOutLoading: false,
                    logOutError: action.error,
            } 
        default:
            return state
    }
}

export default reducer
