import { EllipsisOutlined, HeartOutlined, MessageOutlined, PlusOutlined, RetweetOutlined } from "@ant-design/icons"
import { Button, Card, Popover, } from "antd"
import React, { useState,useCallback } from "react"
import { useSelector } from "react-redux"
import PropTypes from 'prop-types'
import Avatar from "antd/lib/avatar/avatar"
import ImagesZoom from "./ImagesZoom/Index"

const PostImages = ({ images }) => {
    const [showImagesZoom, setShowImagesZoom] = useState(false)
    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, [])
    const onClose = useCallback(() => {
        setShowImagesZoom(false)
    })

    if (images.length === 1) {
    return (
        <> 
          <img role="presentation" style={{ width: '50%', display: 'inline-block' }} src={`http://localhost:3065/%{images[0].src}`} alt={images[0].src} onClick={onZoom} />
          {showImagesZoom && <ImagesZoom images={images} onClose={onClose} />}
        </>
    )
    }
    if (images.length === 2) {
        return (
            <> 
            <img role="presentation" style={{ width: '50%', display: 'inline-block' }} src={`http://localhost:3065/${images[0].src}`} onClick={onZoom} />
            <img role="presentation" style={{ width: '50%', display: 'inline-block' }} src={`http://localhost:3065/${images[1].src}`} onClick={onZoom} />
            {showImagesZoom && <ImagesZoom images={images} onClose={onClose} />}
            </>
        )
    }
    return (
    <> 
        <div>
            <img role="presentation" style={{ height: '30%', display: 'inline-block' }} width="30%" src={`http://localhost:3065/${images[0].src}`} alt={images[0].src} onClick={onZoom} />
            <div
                role="presentation"
                style={{ display: 'inline-block', height: '50%', textAlign: 'center', verticalAlign: 'middle' }}
                width="30%"
                onClick={onZoom}
                >
                <PlusOutlined />
                <br />
                {images.length -1}
                개의 사진 더버기
            </div>
        </div>
        {showImagesZoom && <ImagesZoom images={images} onClose={onClose} />}
        </>
        )  
}

PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
}

export default PostImages
