const reducer = (state = initialState, action) => {
    switch (action.type) {
        case LOG_IN_REQUEST:
            return {
                    ...state,
                    logInLoading: true,
                    logInError: null,
                    logInDone: false,
            }
        case LOG_IN_SUCCESS:
            return {
                    ...state,
                    logInLoading: false,
                    logInDone: true,
                    me: dummyUser(action.data),                
            }
        case LOG_IN_FAILURE:
            return {
                    ...state,
                    logInLoading: false,
                    logInDone: action.error,
            }
        case LOG_OUT_REQUEST:
            return {
                    ...state,
                    logOutLoading: true,
                    logOutDone: false,
                    logOutError: null
            }
        case LOG_OUT_SUCCESS:
            return {
                    ...state,
                    logOutLoading: false,
                    logOutDone: true,
                    logInLoading: false,
                    me: null,
            }
        case LOG_OUT_FAILURE:
            return {
                    ...state,
                    logOutLoading: false,
                    logOutError: action.error,
            }
        case SIGN_UP_REQUEST:
            return {
                    ...state,
                    singUpLoading: true,
                    singUpDone: false,
                    singUpError: null
            }
        case SIGN_UP_SUCCESS:
            return {
                    ...state,
                    singUpLoading: false,
                    singUpDone: true,
            }
        case SIGN_UP_FAILURE:
            return {
                    ...state,
                    singUpLoading: false,
                    singUpError: action.error,
            }      
        default:{
            return {
                ...state
            }
        }

    }
}

export default reducer
