export const initialState = {
    mainPosts: [{
        id: 1,
        User: {
            id: 1,
            nickname: 'HonestJJ',
        },
        content: ' My first 게시물 ',
        Images: [{
                src: 'https://blog.kakaocdn.net/dn/HCIOU/btq7bg2FCBu/uEGQkmTyLMctBqPlcrRXMK/img.jpg'
            },  {
                src: 'https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2FeqqBpc%2Fbtq7bDXOxzg%2Fm9K4sPYXsOme3nkPVK2wkk%2Fimg.png'
            },  {
                src: 'https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2FwXZdy%2Fbtq66VEXbbk%2FQqb7YtIpKNp7VhzaOuu751%2Fimg.png'
            }],
        Comments: [{
            User: {
                nickname: 'Jake'
            },
                content: '리액트 1',
            }, {
            User: {
                nickname: 'Honest JJ'
            },
                content: '리액트 2'
            }]
    }],
    imagePaths: [],
    postAdded: false,
}

const ADD_POST = 'ADD_POST';
export const addPost = {
    type: ADD_POST,
}

const dummyPost = {
    id:2,
    content: '더미 데이터1',
    User: {
        id:1,
        nickname: 'HonestJJ',
    },
    Images: [],
    Comments: [],
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST:
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts]
            }
        default:
            return state
    }
}

export default reducer
