import { EllipsisOutlined, HeartOutlined, MessageOutlined, PlusOutlined, RetweetOutlined } from "@ant-design/icons"
import { Button, Card, Popover, } from "antd"
import React, { useState,useCallback } from "react"
import { useSelector } from "react-redux"
import PropTypes from 'prop-types'
import Avatar from "antd/lib/avatar/avatar"

const PostImages = ({ images }) => {
    const [showImagesZoom, setShowImagesZoom] = useState(false)
    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, [])
    console.log('test', images )
    if (images.length === 1) {
    return (
        <> 
          <img role="presentation" style={{ width: '50%' }} src={images[0].src} alt={images[0].src} onClick={onZoom} />
        </>
    )
    }
    if (images.length === 2) {
        <> 
          <img role="presentation" style={{ width: '50%' }} src={images[0].src} alt={images[0].src} onClick={onZoom} />
          <img role="presentation" style={{ width: '50%' }} src={images[1].src} alt={images[1].src} onClick={onZoom} />
        </>
        }
    return (
       <> 
        <div>
            <img role="presentation" style={{ width: '50%' }} width="50%" src={images[0].src} alt={images[0].src} onClick={onZoom} />
            <div
                role="presentation"
                style={{ display: 'inline-block', width: '50%', textAlign: 'center', verticalAlign: 'middle' }}
                onClick={onZoom}
                >
                <PlusOutlined />
                <br />
                {images.length -1}
                개의 사진 더버기
            </div>
          </div>
        </>
        )
}

PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
}

export default PostImages
